import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { FiEye, FiEyeOff, FiSun, FiMoon } from 'react-icons/fi';
import { FaGoogle, FaMicrosoft, FaApple } from 'react-icons/fa';
import { signInWithEmailAndPassword, signInWithPopup } from 'firebase/auth';
import { auth, googleProvider, microsoftProvider, appleProvider } from '../utils/firebase';
import { setUser } from '../store/authSlice';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isDark, setIsDark] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    const theme = localStorage.getItem('theme');
    if (theme === 'dark') {
      setIsDark(true);
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleTheme = () => {
    setIsDark(!isDark);
    if (!isDark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const userCredential = await signInWithEmailAndPassword(auth, formData.email, formData.password);
      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: userCredential.user.displayName,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Invalid email or password');
    } finally {
      setLoading(false);
    }
  };

  const handleSocialLogin = async (provider) => {
    setLoading(true);
    setError('');

    try {
      const userCredential = await signInWithPopup(auth, provider);
      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: userCredential.user.displayName,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Social login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen">
      {/* Simple Header */}
      <header className="bg-header backdrop-blur-lg sticky top-0 z-50 border-b border-main">
        <nav className="container mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center">
              <Link to="/" className="flex items-center space-x-3">
                <span className="text-2xl md:text-3xl font-bold">
                  <span className="hero-shoe">SHOE</span>
                  <span className="hero-collection"> COLLECTION</span>
                </span>
              </Link>
            </div>
            <div className="hidden lg:flex items-center space-x-2 font-medium">
              <Link to="/" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">HOME</Link>
              <Link to="/shop" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">COLLECTION</Link>
              <Link to="/about" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">ABOUT US</Link>
            </div>
            <div className="flex items-center space-x-2 sm:space-x-4">
              <Link to="/cart" className="p-2 rounded-full hover-custom-gray transition-colors">
                <svg className="w-6 h-6 fill-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                  <path d="M7 18c-1.1 0-1.99.9-1.99 2S5.9 22 7 22s2-.9 2-2-.9-2-2-2zM1 2v2h2l3.6 7.59-1.35 2.45c-.16.28-.25.61-.25.96 0 1.1.9 2 2 2h12v-2H7.42c-.14 0-.25-.11-.25-.25l.03-.12.9-1.63h7.45c.75 0 1.41-.41 1.75-1.03l3.58-6.49c.08-.14.12-.31.12-.48 0-.55-.45-1-1-1H5.21l-.94-2H1zm16 16c-1.1 0-1.99.9-1.99 2s.89 2 1.99 2 2-.9 2-2-.9-2-2-2z"/>
                </svg>
              </Link>
              <button onClick={toggleTheme} className="p-2 rounded-full hover-custom-gray transition-colors">
                {isDark ? <FiSun className="w-6 h-6 fill-icon" /> : <FiMoon className="w-6 h-6 fill-icon" />}
              </button>
            </div>
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <h2 className="text-3xl font-bold text-main">Welcome Back</h2>
            <p className="mt-2 text-secondary">Login to your account</p>
          </div>

          <form onSubmit={handleSubmit} className="bg-card p-8 rounded-lg shadow-lg space-y-6">
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
                {error}
              </div>
            )}

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-secondary mb-2">Email Address</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-input" 
                placeholder="you@example.com" 
                required 
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-secondary mb-2">Password</label>
              <div className="relative">
                <input 
                  type={showPassword ? 'text' : 'password'} 
                  id="password" 
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="form-input pr-10" 
                  placeholder="••••••••" 
                  required 
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-secondary"
                >
                  {showPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <label className="flex items-center">
                <input 
                  type="checkbox" 
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                  className="h-4 w-4 rounded border-main text-accent focus:ring-accent"
                />
                <span className="ml-2 text-sm text-secondary">Remember me</span>
              </label>
              <Link to="/forgot-password" className="text-sm text-accent hover:text-accent-hover">Forgot password?</Link>
            </div>

            <button 
              type="submit" 
              disabled={loading}
              className="w-full bg-accent text-btn py-3 rounded-lg hover:bg-accent-hover transition-colors font-semibold disabled:opacity-50"
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>

            {/* Social Login */}
            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-main" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-card text-secondary">Or continue with</span>
                </div>
              </div>

              <div className="mt-6 grid grid-cols-3 gap-3">
                <button
                  type="button"
                  onClick={() => handleSocialLogin(googleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaGoogle className="text-red-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(microsoftProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaMicrosoft className="text-blue-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(appleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaApple className="text-black" size={20} />
                </button>
              </div>
            </div>

            <div className="text-center">
              <p className="text-sm text-secondary">
                Don't have an account? 
                <Link to="/signup" className="text-accent hover:text-accent-hover font-medium ml-1">Sign up</Link>
              </p>
            </div>
          </form>
        </div>
      </main>
    </div>
  );
};

export default Login;
