import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setProducts, setLoading } from '../store/productsSlice';
import ProductCard from '../components/ProductCard';

const Women = () => {
  const { items, loading } = useSelector(state => state.products);
  const dispatch = useDispatch();
  const [openFilters, setOpenFilters] = useState({});

  // Filter products for women's category
  const womenProducts = items.filter(product => product.category === 'women' || product.gender === 'women');

  useEffect(() => {
    if (items.length === 0) {
      const mockProducts = [
        {
          id: 1,
          name: "Puma Runner",
          price: 120,
          rating: 4.5,
          reviews: 128,
          category: "Sneakers",
          gender: "men",
          brand: "PUMA",
          type: "sneakers",
          image: "/images/shoes/nike-air-max.jpg",
          sizes: [7, 8, 9, 10, 11, 12],
          inStock: true
        },
        {
          id: 2,
          name: "Campus Sneaker",
          price: 85,
          rating: 4.2,
          reviews: 95,
          category: "Casual",
          gender: "men",
          brand: "CAMPUS",
          type: "casual-shoes",
          image: "/images/casual/campus-sneaker.jpg",
          sizes: [6, 7, 8, 9, 10, 11],
          inStock: true
        },
        {
          id: 3,
          name: "Bata Formal",
          price: 150,
          rating: 4.4,
          reviews: 67,
          category: "Formal",
          gender: "men",
          image: "https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcT2xWqfA1u034Kue8aeBZlJnsHGjZ-IYV6lWi4luHDBxrec2Va1iqSmKqljgPttOA4Jmg0pDRtibUQXJcvwwEmbe7KE12JFZauWmgsHWBacUPzd43jHkTu6XA",
          sizes: [8, 9, 10, 11, 12],
          inStock: true
        },
        {
          id: 4,
          name: "Hiking Boot",
          price: 180,
          rating: 4.6,
          reviews: 89,
          category: "Sports",
          gender: "men",
          brand: "NIKE",
          type: "boots",
          image: "/images/boots/leather-boots.jpg",
          sizes: [7, 8, 9, 10, 11],
          inStock: true
        },
        {
          id: 5,
          name: "Suede Loafer",
          price: 95,
          rating: 4.3,
          reviews: 76,
          category: "Casual",
          gender: "men",
          image: "https://5.imimg.com/data5/SELLER/Default/2021/3/UM/PE/DX/117689667/20210310-120703-500x500.jpg",
          sizes: [7, 8, 9, 10, 11],
          inStock: true
        },
        {
          id: 6,
          name: "Spark Sandal",
          price: 60,
          rating: 4.1,
          reviews: 54,
          category: "Sandals",
          gender: "men",
          brand: "PARAGON",
          type: "sandals",
          image: "/images/sandals/summer-sandals.jpg",
          sizes: [6, 7, 8, 9, 10, 11],
          inStock: true
        },
        {
          id: 7,
          name: "Adidas Sneaker",
          price: 85,
          rating: 4.5,
          reviews: 112,
          category: "Sneakers",
          gender: "men",
          image: "https://rukminim2.flixcart.com/image/704/844/xif0q/shoe/v/x/j/-original-imah9mg7zhy5e43n.jpeg?q=90&crop=false",
          sizes: [7, 8, 9, 10, 11, 12],
          inStock: true
        },
        {
          id: 8,
          name: "Paragon Slippers",
          price: 35,
          rating: 4.0,
          reviews: 89,
          category: "Slippers",
          gender: "men",
          image: "https://rukminim3.flixcart.com/image/824/972/l1b1oy80/shopsy-slipper-flip-flop/3/f/w/9-tan-6230-paragon-tan-original-imagcwtary8n2my7.jpeg?q=60&crop=false",
          sizes: [6, 7, 8, 9, 10, 11],
          inStock: true
        }
      ];

      dispatch(setLoading(true));
      setTimeout(() => {
        dispatch(setProducts(mockProducts));
      }, 500);
    }
  }, [dispatch, items.length]);

  const toggleFilter = (filterName) => {
    setOpenFilters(prev => ({
      ...prev,
      [filterName]: !prev[filterName]
    }));
  };

  return (
    <div className="py-16">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Women's Collection</h1>
          <p className="text-lg text-secondary">Elegant footwear designed for the modern woman</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          <aside className="lg:col-span-1">
            <div className="bg-card p-4 rounded-lg shadow-md">
              <h3 className="text-xl font-bold mb-4">Filters</h3>

              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('price')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Price</span>
                  <span>{openFilters.price ? '-' : '+'}</span>
                </button>
                {openFilters.price && (
                  <div className="mt-2">
                    <input type="range" className="w-full" min="50" max="500" />
                    <div className="flex justify-between text-sm text-secondary mt-1">
                      <span>$50</span>
                      <span>$500</span>
                    </div>
                  </div>
                )}
              </div>

              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('size')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Size</span>
                  <span>{openFilters.size ? '-' : '+'}</span>
                </button>
                {openFilters.size && (
                  <div className="mt-2">
                    <div className="grid grid-cols-4 gap-2 text-center">
                      {[5, 6, 7, 8, 9, 10, 11, 12].map(size => (
                        <button key={size} className="border border-main rounded p-2 hover:bg-gray-200 dark:hover:bg-gray-700">
                          {size}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('type')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Type</span>
                  <span>{openFilters.type ? '-' : '+'}</span>
                </button>
                {openFilters.type && (
                  <div className="mt-2 space-y-2">
                    {['Sandals', 'Flip-Flops', 'Flats', 'Heels', 'Kolhapuri Chappals', 'Sneakers', 'Sports Shoes'].map(type => (
                      <label key={type} className="flex items-center space-x-2 cursor-pointer">
                        <input type="checkbox" className="h-4 w-4 rounded text-accent focus:ring-accent" />
                        <span>{type}</span>
                      </label>
                    ))}
                  </div>
                )}
              </div>

              <div className="mt-6">
                <button className="w-full bg-accent text-btn py-2 rounded-lg hover:bg-accent-hover transition-colors font-semibold">
                  Apply Filters
                </button>
              </div>
            </div>
          </aside>

          <div className="lg:col-span-3">
            {loading ? (
              <div className="flex justify-center items-center h-64">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-accent"></div>
              </div>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
                {womenProducts.map(product => (
                  <ProductCard key={product.id} product={product} />
                ))}
                {womenProducts.length === 0 && (
                  <div className="col-span-full text-center py-12">
                    <p className="text-secondary text-lg">No women's products found.</p>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Women;
