import React, { useState } from 'react';

const Men = () => {
  const [filters, setFilters] = useState({
    price: [50, 500],
    sizes: [],
    types: []
  });
  const [openFilters, setOpenFilters] = useState({});

  const products = [
    {
      id: 1,
      name: "Puma Men's Runner",
      price: 120,
      image: "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcREGqtp-YYJUtbYD6ShIVu7GHNbEIZTN3TfCqVSDDdr_VyVZF74WbjTYT8AX33ox5lhpipW0pw2ZoP-E-WJwQzGOMdmaLmAjJ10BQGH4bYgH4wqjzIILGfsNSw"
    },
    {
      id: 2,
      name: "Bata Men's Formal",
      price: 150,
      image: "https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcT2xWqfA1u034Kue8aeBZlJnsHGjZ-IYV6lWi4luHDBxrec2Va1iqSmKqljgPttOA4Jmg0pDRtibUQXJcvwwEmbe7KE12JFZauWmgsHWBacUPzd43jHkTu6XA"
    },
    {
      id: 3,
      name: "Men's Hiking Boot",
      price: 180,
      image: "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcQccPxxyoKylntrVc677XvvX9AZ_uDYiVWMiczmIqAVhLeP_5ladjQNpaL4vbBuv-BdfZ0R2u5_V3J8OrwpSIx51VleSjZe2DesbRnoISDlll8yy1zrrujpfAs"
    },
    {
      id: 4,
      name: "Men's Suede Loafer",
      price: 95,
      image: "https://5.imimg.com/data5/SELLER/Default/2021/3/UM/PE/DX/117689667/20210310-120703-500x500.jpg"
    },
    {
      id: 5,
      name: "Adidas Men's Sneaker",
      price: 85,
      image: "https://rukminim2.flixcart.com/image/704/844/xif0q/shoe/v/x/j/-original-imah9mg7zhy5e43n.jpeg?q=90&crop=false"
    },
    {
      id: 6,
      name: "Campus Men's Sneaker",
      price: 85,
      image: "https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcSBzSAW6LCeZ2XQE0LVGhlck5kT4cxRH2ImKbFv1rTEMMVUPkJ8hKU7Ke65bblQyD-S0SFn15ciXsFwRTj1ygn6vGOBCd47nyNAR6I8sBHMR-eyvP4DD_vdOw"
    }
  ];

  const toggleFilter = (filterName) => {
    setOpenFilters(prev => ({
      ...prev,
      [filterName]: !prev[filterName]
    }));
  };

  return (
    <div className="py-16">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Men's Collection</h1>
          <p className="text-lg text-secondary">Discover premium footwear designed for the modern man</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Filter Sidebar */}
          <aside className="lg:col-span-1">
            <div className="bg-card p-4 rounded-lg shadow-md">
              <h3 className="text-xl font-bold mb-4">Filters</h3>

              {/* Price Filter */}
              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('price')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Price</span>
                  <span>{openFilters.price ? '-' : '+'}</span>
                </button>
                {openFilters.price && (
                  <div className="mt-2">
                    <input type="range" className="w-full" min="50" max="500" />
                    <div className="flex justify-between text-sm text-secondary mt-1">
                      <span>$50</span>
                      <span>$500</span>
                    </div>
                  </div>
                )}
              </div>

              {/* Size Filter */}
              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('size')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Size</span>
                  <span>{openFilters.size ? '-' : '+'}</span>
                </button>
                {openFilters.size && (
                  <div className="mt-2">
                    <div className="grid grid-cols-4 gap-2 text-center">
                      {[7, 8, 9, 10, 11, 12, 13, 14].map(size => (
                        <button key={size} className="border border-main rounded p-2 hover:bg-gray-200 dark:hover:bg-gray-700">
                          {size}
                        </button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Type Filter */}
              <div className="border-b border-main pb-4 mb-4">
                <button 
                  onClick={() => toggleFilter('type')}
                  className="w-full flex justify-between items-center font-semibold"
                >
                  <span>Type</span>
                  <span>{openFilters.type ? '-' : '+'}</span>
                </button>
                {openFilters.type && (
                  <div className="mt-2 space-y-2">
                    {['Sandals', 'Flip-Flops', 'Slippers', 'Boots', 'Formal Shoes', 'Casual Shoes', 'Loafers', 'Sneakers', 'Sports Shoes'].map(type => (
                      <label key={type} className="flex items-center space-x-2 cursor-pointer">
                        <input type="checkbox" className="h-4 w-4 rounded text-accent focus:ring-accent" />
                        <span>{type}</span>
                      </label>
                    ))}
                  </div>
                )}
              </div>

              <div className="mt-6">
                <button className="w-full bg-accent text-btn py-2 rounded-lg hover:bg-accent-hover transition-colors font-semibold">
                  Apply Filters
                </button>
              </div>
            </div>
          </aside>

          {/* Products Grid */}
          <div className="lg:col-span-3">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
              {products.map(product => (
                <div key={product.id} className="bg-card rounded-lg shadow-md overflow-hidden group">
                  <img 
                    src={product.image} 
                    alt={product.name}
                    className="w-full h-64 object-cover transform group-hover:scale-105 transition-transform duration-300"
                  />
                  <div className="p-4">
                    <h3 className="font-semibold text-lg">{product.name}</h3>
                    <p className="text-secondary">${product.price}.00</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Men;
