import { createSlice } from '@reduxjs/toolkit';

const productsSlice = createSlice({
  name: 'products',
  initialState: {
    items: [],
    filteredItems: [],
    categories: ['All', 'Sneakers', 'Sports', 'Casual', 'Formal'],
    loading: false,
    searchTerm: '',
    selectedCategory: 'All',
    sortBy: 'name',
  },
  reducers: {
    setProducts: (state, action) => {
      state.items = action.payload;
      state.filteredItems = action.payload;
      state.loading = false;
    },
    setLoading: (state, action) => {
      state.loading = action.payload;
    },
    setSearchTerm: (state, action) => {
      state.searchTerm = action.payload;
      state.filteredItems = state.items.filter(product =>
        product.name.toLowerCase().includes(action.payload.toLowerCase()) &&
        (state.selectedCategory === 'All' || product.category === state.selectedCategory)
      );
    },
    setCategory: (state, action) => {
      state.selectedCategory = action.payload;
      state.filteredItems = state.items.filter(product =>
        (action.payload === 'All' || product.category === action.payload) &&
        product.name.toLowerCase().includes(state.searchTerm.toLowerCase())
      );
    },
    setSortBy: (state, action) => {
      state.sortBy = action.payload;
      state.filteredItems.sort((a, b) => {
        if (action.payload === 'price-low') return a.price - b.price;
        if (action.payload === 'price-high') return b.price - a.price;
        if (action.payload === 'rating') return b.rating - a.rating;
        return a.name.localeCompare(b.name);
      });
    },
    addProduct: (state, action) => {
      state.items.push(action.payload);
      state.filteredItems = state.items.filter(product =>
        (state.selectedCategory === 'All' || product.category === state.selectedCategory) &&
        product.name.toLowerCase().includes(state.searchTerm.toLowerCase())
      );
    },
    deleteProduct: (state, action) => {
      state.items = state.items.filter(p => p.id !== action.payload);
      state.filteredItems = state.items.filter(product =>
        (state.selectedCategory === 'All' || product.category === state.selectedCategory) &&
        product.name.toLowerCase().includes(state.searchTerm.toLowerCase())
      );
    },
  },
});

export const { setProducts, setLoading, setSearchTerm, setCategory, setSortBy, addProduct, deleteProduct } = productsSlice.actions;
export default productsSlice.reducer;
