import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { FiEye, FiEyeOff } from 'react-icons/fi';
import { FaGoogle, FaMicrosoft, FaApple } from 'react-icons/fa';
import { createUserWithEmailAndPassword, signInWithPopup, updateProfile } from 'firebase/auth';
import { auth, googleProvider, microsoftProvider, appleProvider } from '../utils/firebase';
import { setUser } from '../store/authSlice';

const Signup = () => {
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      await updateProfile(userCredential.user, { displayName: formData.fullname });

      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: formData.fullname,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Failed to create account. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSocialLogin = async (provider) => {
    setLoading(true);
    setError('');

    try {
      const userCredential = await signInWithPopup(auth, provider);
      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: userCredential.user.displayName,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Social login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <Link to="/" className="inline-block mb-6">
            <span className="text-2xl md:text-3xl font-bold">
              <span className="hero-shoe">SHOE</span>
              <span className="hero-collection"> COLLECTION</span>
            </span>
          </Link>
          <h2 className="text-3xl font-bold text-main">Create Account</h2>
          <p className="mt-2 text-secondary">Join the Shoe Collection family</p>
        </div>

          <form onSubmit={handleSubmit} className="bg-card p-8 rounded-lg shadow-lg space-y-6">
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
                {error}
              </div>
            )}

            <div>
              <label htmlFor="fullname" className="block text-sm font-medium text-secondary mb-2">Full Name</label>
              <input 
                type="text" 
                id="fullname" 
                name="fullname"
                value={formData.fullname}
                onChange={handleChange}
                className="form-input" 
                placeholder="John Doe" 
                required 
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-secondary mb-2">Email Address</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-input" 
                placeholder="you@example.com" 
                required 
              />
            </div>

            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-secondary mb-2">Phone Number</label>
              <input 
                type="tel" 
                id="phone" 
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                className="form-input" 
                placeholder="+91 12345 67890" 
                required 
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-secondary mb-2">Password</label>
              <div className="relative">
                <input 
                  type={showPassword ? 'text' : 'password'} 
                  id="password" 
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="form-input pr-10" 
                  placeholder="••••••••" 
                  required 
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-secondary"
                >
                  {showPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            </div>

            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-secondary mb-2">Confirm Password</label>
              <div className="relative">
                <input 
                  type={showConfirmPassword ? 'text' : 'password'} 
                  id="confirmPassword" 
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="form-input pr-10" 
                  placeholder="••••••••" 
                  required 
                />
                <button
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-secondary"
                >
                  {showConfirmPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            </div>

            <button 
              type="submit" 
              disabled={loading}
              className="w-full bg-accent text-btn py-3 rounded-lg hover:bg-accent-hover transition-colors font-semibold disabled:opacity-50"
            >
              {loading ? 'Creating Account...' : 'Create Account'}
            </button>

            {/* Social Login */}
            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-main" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-card text-secondary">Or continue with</span>
                </div>
              </div>

              <div className="mt-6 grid grid-cols-3 gap-3">
                <button
                  type="button"
                  onClick={() => handleSocialLogin(googleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaGoogle className="text-red-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(microsoftProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaMicrosoft className="text-blue-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(appleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaApple className="text-black" size={20} />
                </button>
              </div>
            </div>

            <div className="text-center">
              <p className="text-sm text-secondary">
                Already have an account? 
                <Link to="/login" className="text-accent hover:text-accent-hover font-medium ml-1">Login</Link>
              </p>
            </div>
          </form>
        </div>
    </div>
  );
};

export default Signup;
