import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { FiEye, FiEyeOff, FiSun, FiMoon } from 'react-icons/fi';
import { FaGoogle, FaMicrosoft, FaApple } from 'react-icons/fa';
import { createUserWithEmailAndPassword, signInWithPopup, updateProfile } from 'firebase/auth';
import { auth, googleProvider, microsoftProvider, appleProvider } from '../utils/firebase';
import { setUser } from '../store/authSlice';

const Signup = () => {
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isDark, setIsDark] = useState(false);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    const theme = localStorage.getItem('theme');
    if (theme === 'dark') {
      setIsDark(true);
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleTheme = () => {
    setIsDark(!isDark);
    if (!isDark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
      await updateProfile(userCredential.user, { displayName: formData.fullname });

      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: formData.fullname,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Failed to create account. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSocialLogin = async (provider) => {
    setLoading(true);
    setError('');

    try {
      const userCredential = await signInWithPopup(auth, provider);
      dispatch(setUser({
        uid: userCredential.user.uid,
        email: userCredential.user.email,
        displayName: userCredential.user.displayName,
        photoURL: userCredential.user.photoURL
      }));
      navigate('/');
    } catch (error) {
      setError('Social login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen">
      {/* Simple Header */}
      <header className="bg-header backdrop-blur-lg sticky top-0 z-50 border-b border-main">
        <nav className="container mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center">
              <Link to="/" className="flex items-center space-x-3">
                <span className="text-2xl md:text-3xl font-bold">
                  <span className="hero-shoe">SHOE</span>
                  <span className="hero-collection"> COLLECTION</span>
                </span>
              </Link>
            </div>
            <div className="hidden lg:flex items-center space-x-2 font-medium">
              <Link to="/" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">HOME</Link>
              <Link to="/shop" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">COLLECTION</Link>
              <Link to="/about" className="text-main hover-custom-gray rounded-full px-4 py-2 transition-colors">ABOUT US</Link>
            </div>
            <div className="flex items-center space-x-2 sm:space-x-4">
              <Link to="/cart" className="p-2 rounded-full hover-custom-gray transition-colors">
                <svg className="w-6 h-6 fill-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                  <path d="M7 18c-1.1 0-1.99.9-1.99 2S5.9 22 7 22s2-.9 2-2-.9-2-2-2zM1 2v2h2l3.6 7.59-1.35 2.45c-.16.28-.25.61-.25.96 0 1.1.9 2 2 2h12v-2H7.42c-.14 0-.25-.11-.25-.25l.03-.12.9-1.63h7.45c.75 0 1.41-.41 1.75-1.03l3.58-6.49c.08-.14.12-.31.12-.48 0-.55-.45-1-1-1H5.21l-.94-2H1zm16 16c-1.1 0-1.99.9-1.99 2s.89 2 1.99 2 2-.9 2-2-.9-2-2-2z"/>
                </svg>
              </Link>
              <Link to="/login" className="p-2 rounded-full hover-custom-gray transition-colors">
                <svg className="w-6 h-6 fill-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                  <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
                </svg>
              </Link>
              <button onClick={toggleTheme} className="p-2 rounded-full hover-custom-gray transition-colors">
                {isDark ? <FiSun className="w-6 h-6 fill-icon" /> : <FiMoon className="w-6 h-6 fill-icon" />}
              </button>
            </div>
          </div>
        </nav>
      </header>

      {/* Main Content */}
      <main className="min-h-screen flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <h2 className="text-3xl font-bold text-main">Create Account</h2>
            <p className="mt-2 text-secondary">Join the Shoe Collection family</p>
          </div>

          <form onSubmit={handleSubmit} className="bg-card p-8 rounded-lg shadow-lg space-y-6">
            {error && (
              <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
                {error}
              </div>
            )}

            <div>
              <label htmlFor="fullname" className="block text-sm font-medium text-secondary mb-2">Full Name</label>
              <input 
                type="text" 
                id="fullname" 
                name="fullname"
                value={formData.fullname}
                onChange={handleChange}
                className="form-input" 
                placeholder="John Doe" 
                required 
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-secondary mb-2">Email Address</label>
              <input 
                type="email" 
                id="email" 
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="form-input" 
                placeholder="you@example.com" 
                required 
              />
            </div>

            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-secondary mb-2">Phone Number</label>
              <input 
                type="tel" 
                id="phone" 
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                className="form-input" 
                placeholder="+91 12345 67890" 
                required 
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-secondary mb-2">Password</label>
              <div className="relative">
                <input 
                  type={showPassword ? 'text' : 'password'} 
                  id="password" 
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className="form-input pr-10" 
                  placeholder="••••••••" 
                  required 
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-secondary"
                >
                  {showPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            </div>

            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-secondary mb-2">Confirm Password</label>
              <div className="relative">
                <input 
                  type={showConfirmPassword ? 'text' : 'password'} 
                  id="confirmPassword" 
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className="form-input pr-10" 
                  placeholder="••••••••" 
                  required 
                />
                <button
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 text-secondary"
                >
                  {showConfirmPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            </div>

            <button 
              type="submit" 
              disabled={loading}
              className="w-full bg-accent text-btn py-3 rounded-lg hover:bg-accent-hover transition-colors font-semibold disabled:opacity-50"
            >
              {loading ? 'Creating Account...' : 'Create Account'}
            </button>

            {/* Social Login */}
            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-main" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-card text-secondary">Or continue with</span>
                </div>
              </div>

              <div className="mt-6 grid grid-cols-3 gap-3">
                <button
                  type="button"
                  onClick={() => handleSocialLogin(googleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaGoogle className="text-red-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(microsoftProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaMicrosoft className="text-blue-500" size={20} />
                </button>

                <button
                  type="button"
                  onClick={() => handleSocialLogin(appleProvider)}
                  className="w-full inline-flex justify-center py-2 px-4 border border-main rounded-md shadow-sm bg-card text-sm font-medium text-secondary hover:bg-gray-50 transition-colors"
                >
                  <FaApple className="text-black" size={20} />
                </button>
              </div>
            </div>

            <div className="text-center">
              <p className="text-sm text-secondary">
                Already have an account? 
                <Link to="/login" className="text-accent hover:text-accent-hover font-medium ml-1">Login</Link>
              </p>
            </div>
          </form>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-footer border-t border-main pt-16 pb-8">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mb-8">
            <div className="space-y-4">
              <h3 className="font-semibold text-lg text-main">Support</h3>
              <Link to="/store" className="block text-secondary hover:text-accent transition-colors">Find A Store</Link>
              <Link to="/feedback" className="block text-secondary hover:text-accent transition-colors">Send us Feedback</Link>
              <Link to="/contact" className="block text-secondary hover:text-accent transition-colors">Contact Us</Link>
            </div>
            <div className="space-y-4">
              <h3 className="font-semibold text-lg text-main">About Shoe Collection</h3>
              <Link to="/news" className="block text-secondary hover:text-accent transition-colors">News</Link>
              <Link to="/track" className="block text-secondary hover:text-accent transition-colors">Track Order</Link>
              <Link to="/careers" className="block text-secondary hover:text-accent transition-colors">Careers</Link>
            </div>
            <div className="space-y-4">
              <h3 className="font-semibold text-lg text-main">Legal</h3>
              <Link to="/terms" className="block text-secondary hover:text-accent transition-colors">Terms & Conditions</Link>
              <Link to="/privacy" className="block text-secondary hover:text-accent transition-colors">Privacy Policy</Link>
            </div>
            <div className="space-y-4">
