import React from 'react';
import { Link } from 'react-router-dom';

const Kids = () => {
  const collections = [
    {
      title: 'Bestselling',
      image: 'https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=bestselling'
    },
    {
      title: 'Trending',
      image: 'https://images.unsplash.com/photo-1514989940723-e8e51635b782?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=trending'
    },
    {
      title: 'Latest Collection',
      image: 'https://images.unsplash.com/photo-1603808033192-082d6919d3e1?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=latest'
    },
    {
      title: 'Sports',
      image: 'https://images.unsplash.com/photo-1595461140240-75be4f9f2e11?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=sports'
    },
    {
      title: 'School Shoes',
      image: 'https://images.unsplash.com/photo-1560472354-b33ff0c44a43?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=school'
    },
    {
      title: 'Sandals',
      image: 'https://images.unsplash.com/photo-1506629905607-d405d7d3b0d2?w=400&h=300&fit=crop',
      link: '/shop?category=kids&type=sandals'
    }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center mb-12">
        <h1 className="text-4xl font-bold text-main mb-4">Kids Collection</h1>
        <p className="text-secondary">Fun and comfortable shoes for kids</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {collections.map((collection, index) => (
          <Link 
            key={index}
            to={collection.link}
            className="group relative overflow-hidden rounded-lg shadow-lg hover:shadow-xl transition-all duration-300"
          >
            <div className="aspect-w-16 aspect-h-12">
              <img 
                src={collection.image} 
                alt={collection.title}
                className="w-full h-64 object-cover group-hover:scale-105 transition-transform duration-300"
              />
            </div>
            <div className="absolute inset-0 bg-black bg-opacity-40 group-hover:bg-opacity-50 transition-all duration-300"></div>
            <div className="absolute inset-0 flex items-center justify-center">
              <h3 className="text-white text-2xl font-bold text-center">{collection.title}</h3>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Kids;
